jQuery.placeholder=function(){$("[placeholder]").focus(function(){var a=$(this);a.hasClass("placeholder")&&(a.val(""),a.removeClass("placeholder"))}).blur(function(){var a=$(this);""===a.val()&&(a.addClass("placeholder"),a.val(a.attr("placeholder")))}).blur().parents("form").submit(function(){$(this).find("[placeholder]").each(function(){var a=$(this);a.hasClass("placeholder")&&a.val("")})});$(window).unload(function(){$("[placeholder]").val("")})};
jQuery.fn.addPlaceholder=function(){return this.each(function(){var a=$(this);a.addClass("placeholder");a.val(a.attr("placeholder"))})};$(document).ready(function(){$.placeholder()});
